<?php	class Aboutus extends CI_Controller{		public function __construct(){			parent::__construct();									$this->load->model('Aboutus_model','aboutus');					}				public function showindex(){						$list=$this->aboutus->showindex();			//var_dump($list);exit;			$data=array('list'=>$list);			$this->load->view('content/aboutus',$data);					}				public function edit(){						$id=$this->uri->segment(3);			$data['list']=$this->aboutus->showedit('about',$id);			$this->load->view('content/editaboutus',$data);									}				public function update(){			$data=$_POST;			$id=$this->uri->segment(3);			$bool=$this->aboutus->aboutusupdate('about',$data,$id);			//var_dump($bool);exit;			if($bool===true){				redirect('aboutus/showindex');			}else{				redirect('aboutus/showindex');			}		}				public function delete(){			$id=$this->uri->segment(3);			$bool=$this->db->delete('about',$id);			if($bool==true){				redirect('aboutus/showindex');			}else{				redirect('aboutus/showindex');			}					}	}