<?php	class news extends CI_Controller{		public function __construct(){			parent::__construct();			$this->load->model('news_model','news');					}						public function addnews(){			$this->load->view('content/addnews');		}								public function showindex($num=''){			$this->load->library('pagination');			$config['base_url']= site_url().'/news/shownews/';			$config['total_rows']=$this->news->countnum(); //总数			$config['per_page']=10; //每页显示数量			$config['num_links']=10;//连接前后显示个数			$config['prev_link']='上一页';						$config['next_link']='下一页';						$config['full_tag_open']='<div class="pagination">';			$config['full_tag_close']='</div>';						$this->pagination->initialize($config);//配置分页									$list=$this->news->show($config['per_page'],$num);			$data=array('list'=>$list);			$this->load->view('content/news',$data);					}										public function newsform(){						$bool=$this->news->insertdata();			if($bool==true){								redirect('news/showindex');			}else{								redirect('news/showindex');			}		}				public function edit(){			$id=$this->uri->segment(3);			$list=$this->news->showedit($id);			$data=array('list'=>$list);			$this->load->view('content/editnews',$data);		}				public function newsupdate(){						$bool=$this->news->newsupdate();			if($bool==true){								redirect('news/showindex');			}else{								redirect('news/showindex');			}		}				public function newsdel(){			$id=$this->uri->segment(3);						$bool=$this->news->newsdelete($id);			if($bool==true){												redirect('news/showindex');			}else{								redirect('news/showindex');			}		}			}