<?php	class Down_model extends CI_Model{		public function __construct(){			parent::__construct();					}				public function showindex($table,$page,$num){			$query=$this->db->get($table,$page,$num)->result();			return $query;		}				public function insertdata(){			$data=$_POST;			//var_dump($data);exit;			$bool=$this->db->insert('download',$data);			return $bool;		}				public function showedit($id){			$query=$this->db->where('id',$id)->get('download')->row();			return $query;					}				public function updatedata(){			$id=$_POST['id'];			$data=$_POST;			$bool=$this->db->where('id',$id)->update('download',$data);			return $bool;		}				public function countnum(){			$query=$this->db->count_all('download');			return $query;		}				public function downdelete($id){			$bool=$this->db->where('id',$id)->delete('download');			return $bool;		}			}