INSERT INTO `rs_job` VALUES (2, 1, '网页美工', '<div style="PADDING-BOTTOM: 30px">\r\n	任职要求：<br />\r\n	<br />\r\n	1、美术、艺术、设计、多媒体设计或相关专业，有淘宝店铺工作经验者者优先；<br />\r\n	<br />\r\n	2、熟练掌握AI、Photoshop、coredraw、Dreamweaver GIF动画等网络设计软件，熟悉HTML代码。<br />\r\n	<br />\r\n	3、对设计有自己的风格，需具有一定的文案功底，对于各种活动形式有着深入的了解，同时对视觉也有敏锐的鉴赏力，要求工作认真，努力，对设计有自己的理解。<br />\r\n	<br />\r\n	4、热爱页面设计，沟通能力好、责任心强，有团队合作精神、较强的上进心和学习能力<br />\r\n	<br />\r\n	5、有一定的设计创意和美工基础，能够快速、准确领会设计意图</div>\r\n<p>\r\n	&nbsp;</p>\r\n', '', '', 0, '技术部', '工程师', '深圳', '', 0, 6, 49, 0, 0);INSERT INTO `rs_job` VALUES (10, 1, 'PHP工程师', '<p>\r\n	岗位描述：<br />\r\n	1、负责公司运营网站的开发与测试。<br />\r\n	2、参与项目开发，开发设计网站新功能。<br />\r\n	3、配合网站美工、市场推广、网站销售、客户服务等完成基于网站的各项工作。<br />\r\n	<br />\r\n	岗位要求：<br />\r\n	1、计算机相关专业,2年以上PHP编程开发经验；<br />\r\n	2、精通PHP 5，具备OOP编程思想，熟悉设计模式和算法，具备命令行下开发经验，深入了解PHP的性能最优化和安全最大化,并且有过php mvc框架开发经验；<br />\r\n	3、熟练掌握SQL语言,有一定的数据库规划能力,熟练使用MySQL,能够熟练编写高效率的SQL语句；<br />\r\n	4、熟悉Linux/Unix基本操作，熟悉Apache、MySQL、PHP等软件的编译安装和最优化配置，了解这些软件的版本差异和不同安装/运行方式及原理为佳；<br />\r\n	5、如能精通DHTML、页面DOM、W3C标准、SEO优化、深入了解HTML/XHTML、Jasvascript和CSS在不同类型/版本浏览器下差异者为佳；<br />\r\n	6、具有良好编码风格，工作态度严谨，有较强的独立工作能力与团队合作精神；<br />\r\n	7、能吃苦耐劳，有耐心，强烈责任心和工作激情，能承受较强的工作压力；<br />\r\n	8、拥有良好的工作习惯，自主学习能力强，能够不断接受新生事物，讲究工作方法和效率；</p>\r\n', '', '', 0, '技术部', '工程师', '深圳', '', 368, 50, 1, 0, 0);INSERT INTO `rs_job` VALUES (11, 1, '市场经理', '<p>\r\n	职位描述：<br />\r\n	1、负责区域内销售渠道的开拓及维护；<br />\r\n	2、负责区域内销售产品的行业开发及大客户的维护；<br />\r\n	3、负责区域内销售工作的日常管理，销售团队的组建，销售人员的培养；<br />\r\n	4、负责按期完成公司下达的销售任务指标。<br />\r\n	5、负责完成领导交办的其他工作任务。<br />\r\n	<br />\r\n	<br />\r\n	销售经理主要负责公司代理产品外文电子书销售，产品市场反应非常好，提成比例优厚，忘有经验的你加盟<br />\r\n	职位要求：<br />\r\n	1、本科及以上学历，40岁以下；<br />\r\n	2、IT行业2年以上市场销售经验，具有消费类电子行业销售工作经验者优先；<br />\r\n	3、具有推进销售指标和管理销售团队的能力；<br />\r\n	4、熟悉项目运作的流程，具备良好的沟通及跟进技巧，有成功案例者优先。</p>\r\n', '', '', 3, '市场部', '市场经理', '深圳', '', 0, 6, 68, 0, 0);INSERT INTO `rs_job` VALUES (12, 1, '销售经理', '<div>\r\n	职位描述：<br />\r\n	1、负责区域内销售渠道的开拓及维护；<br />\r\n	2、负责区域内销售产品的行业开发及大客户的维护；<br />\r\n	3、负责区域内销售工作的日常管理，销售团队的组建，销售人员的培养；<br /